const initialState = {
    token: "",
    isAuthenticated: false,
    user: {}
}

const auth = (state = initialState, action) => {
    const { type, payload } = action;

    switch (type) {
        case USER_LOADED:
        localStorage.set('token', payload.token);
            return {
                ...state,
                isAuthenticated: true,
                user: payload,
                token: payload.token
            }

        case USER_AUTH_FAILURE:
            localStorage.removeItem('token');
            return {
                ...state,
                token: "",
                isAuthenticated: false,
                user: {}
            }

        default:
            return state;
    }
}

export default auth;
