const initialState = {
    token: "",
    isAuthenticated: false,
    user: {}
}

const auth = (state = initialState, action) => {
    const { type, payload } = action;
    console.log(payload);

    switch (type) {
        case "LOG_IN_SUCCESS":
            localStorage.setItem('token', payload.token);
            return {
                ...state,
                token: payload.token,
                isAuthenticated: true,
                user: payload
            }

        case "LOG_IN_FAILURE":
            localStorage.removeItem('token');
            return {
                ...state,
                token: null,
                user: {},
                isAuthenticated: false
            }
        default:
            return state;
    }
}

export default auth;
