const initialState = {
    token: "",
    isAuthenticated: false,
    user: {}
}

const auth = (state = initialState, action) => {
    const { type, payload } = action;

    switch (type) {
        case USER_LOADED:

            return {
                ...state,
                isAuthenticated: true,
                user: payload
            }

        case USER_AUTH_FAILURE:
            localStorage.removeItem('token');

            return {
                ...state,
                token: null,
                isAuthenticated: false
            }

        case REGISTER_SUCCESS:
            localStorage.setItem('token', payload.token);

            return {
                ...state,
                ...payload,
                isAuthenticated: true
            }

        case REGISTER_FAILURE:
            localStorage.removeItem('token');

            return {
                ...state,
                token: null,
                isAuthenticated: false
            }

        case LOGIN_SUCCESS:
            localStorage.setItem('token', payload.token);

            return {
                ...state,
                isAuthenticated: true,
                ...payload
            }

        case LOGIN_FAILURE:
            localStorage.removeItem('token');

            return {
                ...state,
                isAuthenticated: false,
                user: null
            }

        default:
            return state;
    }
}

export default auth;
