const initialState = {
    token: "",
    isAuthenticated: false,
    user: {}
}

const auth = (state = initialState, action) => {
    const { type, payload } = action;

    switch (type) {
        case USER_LOADED:

            return {
                ...state,
                isAuthenticated: true,
                user: payload
            }

        case USER_AUTH_FAILURE:
            localStorage.removeItem('token');

            return {
                ...state,
                token: "",
                isAuthenticated: false
            }

        default:
            return state;
    }
}

export default auth;
